export default {
  name: 'App',
  watch: {
    $route(to, from) {
      if (to.path != '/login') {
        let obj = {
          name: to.name,
          title: to.meta.title,
          icon: to.meta.icon,
          closable: true
        };
        this.$store.commit('ADD_TABS', obj);
      }
    }
  },
  mounted() {
    this.$store.commit('INIT_TABS');
  }
};
